#include <iostream>
using namespace std;

// Recursive function to find the nth Fibonacci number
int fibonacci(int n) {
    if (n <= 0) return 0;        // Base case: fib(0) = 0
    else if (n == 1) return 1;   // Base case: fib(1) = 1
    else
        return fibonacci(n - 1) + fibonacci(n - 2);
}

